const char html[] = "<!DOCTYPE html><html lang='en'><head><meta charset='UTF-8'><meta http-equiv='X-UA-Compatible' content='IE=edge'><meta name='viewport' content='width=device-width, initial-scale=1.0'><title>快速配网</title><style> * {margin: 0px;padding: 0px;}  .bg {width: 100%;height: 45vh;text-align: center;color: #fff;background-image: linear-gradient(160deg, #0078ff 20%, #002abb 80%);display: flex;justify-content: center;align-items: center;}  .content {width: 80%;max-width: 360px;background: #fff;position: absolute;top: 50%;left: 50%;transform: translate(-50%, -50%);padding: 20px;box-shadow: 0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.3);}  .inputbox {outline-style: none;margin-bottom: 10px;border: 1px solid #ccc;border-radius: 3px;padding: 13px 14px;font-size: 14px;font-weight: 700;width: 100%;box-sizing: border-box;}  .btn {width: 100%;height: 40px;border-width: 0px;border-radius: 3px;background: #1E90FF;cursor: pointer;outline: none;color: white;font-size: 17px;}  .btn:hover {background: #5599FF;}</style></head><body><div class='bg'></div><div class='content'><input class='inputbox' type='text' name='ssid' placeholder='WiFi账号' id='ssid'/><br/><input class='inputbox' type='password' name='password' placeholder='WiFi密码' id='password'/><br/><input type='submit' class='btn' value='提交' onclick='loginFn()'/></div><script> function loginFn() {let ssid = document.getElementById('ssid').value;let password = document.getElementById('password').value;console.log({ssid: ssid, password: password});window.location.href = '/setting/' + ssid + '/' + password;}</script></body></html>";

